function createfigure(ymatrix1, yvector1)
%CREATEFIGURE(YMATRIX1,YVECTOR1)
%  YMATRIX1:  bar matrix data
%  YVECTOR1:  bar yvector

%  Auto-generated by MATLAB on 19-Mar-2009 14:25:58

% Create figure
figure1 = figure('PaperSize',[11 8.5],'PaperOrientation','landscape');

% Create axes
axes1 = axes('Parent',figure1,'XTickLabel',{'VWAP','SSB'},'XTick',[1 2],...
    'Position',[0.13 0.11 0.2984 0.815],...
    'FontSize',16);
% Uncomment the following line to preserve the X-limits of the axes
% xlim([0.6 2.4]);
box('on');
hold('all');

% Create multiple lines using matrix input to bar
bar1 = bar(ymatrix1,'Parent',axes1);
set(bar1(1),'FaceColor',[0.7294 0.8314 0.9569],...
    'EdgeColor',[0.3922 0.4745 0.6353],...
    'DisplayName','Naive compilation');
set(bar1(2),'FaceColor',[1 0.6941 0.3922],'EdgeColor',[0.8706 0.4902 0],...
    'DisplayName','Postgres');

% Create xlabel
xlabel('Workload','FontSize',20);

% Create ylabel
ylabel('log_{10}(Query processing speedup)','FontSize',20);

% Create axes
axes2 = axes('Parent',figure1,'XTickLabel',{'DBToaster','STREAM'},...
    'XTick',[1 2],...
    'Position',[0.5044 0.11 0.2341 0.815],...
    'FontSize',16);
% Uncomment the following line to preserve the X-limits of the axes
% xlim([0.3 2.7]);
% Uncomment the following line to preserve the Y-limits of the axes
% ylim([0 75]);
box('on');
hold('all');

% Create bar
bar(yvector1,'FaceColor',[0.8 1 0.4],'EdgeColor',[0.1686 0.5059 0.3373],...
    'BarWidth',0.6,...
    'Parent',axes2);

% Create xlabel
xlabel({'Query processing on the','Linear Road benchmark'},'FontSize',14);

% Create ylabel
ylabel('Query execution time (secs)','FontSize',20);

% Create legend
legend1 = legend(axes1,'show');
set(legend1,'Location','NorthWest');

